import { Component } from '@angular/core';
import { SvgServiceService } from '../svg-service.service';
import { FormGroup, FormControl } from '@angular/forms';
import { GraphRequest } from '../types/graph_request';
@Component({
  selector: 'app-ast-page',
  templateUrl: './ast-page.component.html',
  styleUrls: ['./ast-page.component.scss']
})
export class AstPageComponent {

  requestForm = new FormGroup({
    program: new FormControl(''),
    encoding: new FormControl('')
  })

  imageString: String[] = []


  constructor(
    private svgService : SvgServiceService
  ) {}

  onSubmit(){
    const formData = this.requestForm.value; 
    const graphreq = {ast:true, ...formData} as GraphRequest
    let observer = {
      next: (data:String[]) => {
        this.imageString = data; 
        console.log(data)
      }
    }
    this.svgService.post(graphreq).subscribe(
      observer)
    }
  }



<form [formGroup] ="requestForm" (ngSubmit)="onSubmit()" >
    <textarea id="programField" formControlName="program">
        Enter your ASP-Program here.
    </textarea>
    <br>
    <label>Options</label>
    <br>
    <label>Show Children </label>
    <br>
<input id="childCheck" type="checkbox"/>
<input name="submit" type="submit"/> 
</form>
<svg [innerHTML]="svgImage"></svg>
